module AbsGram where

-- Haskell module generated by the BNF converter


newtype Ident = Ident String deriving (Eq,Ord,Show)
data Program =
   Prog [Decl] [FunctionDeclaration] Block
  deriving (Eq,Ord,Show)

data Block =
   SBlock [Decl] [FunctionDeclaration] [Stmt]
  deriving (Eq,Ord,Show)

data RBlock =
   SRBlock [Decl] [FunctionDeclaration] [Stmt] Expression
  deriving (Eq,Ord,Show)

data Decl =
   Declr Type Identifier
 | DAssign Type Identifier Expression
 | DConstDec Type Identifier Expression
  deriving (Eq,Ord,Show)

data FunctionDeclaration =
   FDec Identifier [Arguments] Type RBlock
  deriving (Eq,Ord,Show)

data CallArgs =
   Cargs Expression
 | Ref Identifier
  deriving (Eq,Ord,Show)

data Arguments =
   Args Type Identifier
  deriving (Eq,Ord,Show)

data Stmt =
   SAssign Assignment
 | SExp Expression
 | SWhile Exp Block
 | SFor Exp Exp Identifier Block
 | SGuard [Identifier] Block
 | Sprint Exp
 | SprintS String
 | SIf If
 | SIfE If Block
  deriving (Eq,Ord,Show)

data If =
   If Exp Block [EIf]
  deriving (Eq,Ord,Show)

data EIf =
   SEIf Exp Block
  deriving (Eq,Ord,Show)

data Expression =
   Exp Exp
 | ExpTer Exp Exp Exp
  deriving (Eq,Ord,Show)

data Exp =
   Eor Exp Exp
 | Eand Exp Exp
 | Eeq Exp Exp
 | Elt Exp Exp
 | Egt Exp Exp
 | EAdd Exp Exp
 | ESub Exp Exp
 | EMul Exp Exp
 | EDiv Exp Exp
 | EMinus Exp
 | Call Identifier [CallArgs]
 | EVar Identifier
 | EInt Integer
 | Etrue
 | Efalse
  deriving (Eq,Ord,Show)

data Assignment =
   Assign Identifier Expression
 | AArith Identifier ArAssign Expression
 | AIncDec Identifier IncDec
  deriving (Eq,Ord,Show)

data Type =
   TInt
 | TBool
  deriving (Eq,Ord,Show)

data Identifier =
   Id Ident
  deriving (Eq,Ord,Show)

data ArAssign =
   AAPlus
 | AAMinus
 | AAMulti
 | AADiv
  deriving (Eq,Ord,Show)

data IncDec =
   Increment
 | Decrement
  deriving (Eq,Ord,Show)

